require "pry-rails"

text = "(РЬДЬВЬЛ ЫЖХ) Й ВЭЫШДЕП ЙШЩКГШП ЩШАИДЙЭ Э ЙЬЮЩЬФЬЫКОВС ПЬШНЬ КЩЖНО. ЦЕДЬ ЬХЬДЬ ТШВЕЩШЗ ТОЙЬФ КУЭ, ХЬНКО ПЕ ЮЖЙВИДИЙС Ф ЮЖВС - ДШНЩОУ, БЮИВШЩ, ЙЬЦОХО И Э. БЮИВШЩ УШЙ ХЬЙЖ И ДЬЮОВЕ; ЬУ УОЙВЬЭД УО ЧВЬП УШ ЬВ ИРЦЕВХО ДБЦШРУЬЙВИ ИДИ ЫШ ЮЩИДШЫОУИЭ, УЬ, ХОХ Э ЮЬДОНОБ, ИР ЙВЩОЗО КЬФШЩИВС ЧВИ ЬЩЖКИЭ ЙФЬШПЖ НЬЙЮЬКИУЖ. ФИК Ж УШНЬ ЦЕД ЮЩШЖЮЩЭПЕЛ. \"ТШЩВЬФ ЫЖХ!\" - ФЬВ ШКИУЙВФШУУЬШ, ТВЬ Э ЖЙДЕАОД ЬВ УШНЬ РО ФЙШ ЮЖВШАШЙВФИШ. ПУШ ЮЬЩЖТИДИ КФО ЮЬВОЛУЕЗ МЬУОЩЭ. ДШНЩОУ УШЙ ЫЖХО. ЫЖХ ЦЕД ЮЩИФЭРОУ Х ХЬУГЖ АУЖЩО, Ю ДШНЩОУ ХЩЖВИД ШНЬ УО ЗЬКЖ, ХОХ РОХДИУОВШДС. ХЬНКО Э РОПШВИД ЧВЬ УЬФЬШ ЭФУЬШ КЬХОРОВШДСЙВФЬ ЦШРЖПИЭ ПЬШНЬ КЩЖНО, Э Й ВЩЖКЬП ЖКШЩЫОДЙЭ ЬВ ЙДШР. ВШП УШ ПШУШШ Э ЮЬХО ЩШАИД УИ Ф ТШП УШ ЮШЩШТИВС ДШНЩОУЖ И ЫКОВС ЙДЖТОЭ, ХЬНКО Э ЙПЬНЖ ЮЩШКЮЩИУЭВС ХОХИШ-ДИЦЬ ЧУШЩНИТУЕШ ПШЩЕ. Э ЮЬЮЕВОДЙЭ УШЙХЬДСХЬ ЩОР РОФЭРОВС ЦШЙШКЖ Ь ГШДЭЗ ЮЬЗЬКО, УЬ ЦШРЖЙЮШАУЬ. ЖНЬФЬЩИФ ПШУЭ ИКВИ ФПШЙВШ Й УИП И КЬФЬДСУЕЛ ЧВИП, ДШНЩОУ, ФИКИПЬ, УШ ЗЬВШД ЦЬДСАШ ФШЙВИ УИХОХИЗ ЩОРНЬФЬЩЬФ, И УО ФЙШ ПЬИ ЩОЙЙЮЩЬЙЕ ЬВФШТОД ЬКУЬЙДЬЫУЬ: \"ЖФИКИП!\" КЬЛКЭ КЬ ПЕЙО, ПЕ ЙШДИ Ф ЭДИХ И ЮШЩШЮЩОФИДИЙС УО ПОВШЩИХ. ЮЬВЬП ФРЬЦЩОДИЙС ЮЬ ФЕЙЬХЬПЖ ЦШЩШНЖ И, ФРЭФ УОЮЩОФДШУИШ УО ЙШФШЩЬ-РОЮОК, ЖНДЖЦИДИЙС Ф КИХИЛ, ЮЖЙВЕУУЕЛ ХЩОЛ, НКШ, ХОРОДЬЙС, УИХЬНКО УШ ЙВЖЮОДО УЬНО ТШДЬФШХО. ДШНЩОУ ЖФШЩШУУЬ ФШД УОЙ ФЮШЩШК, ДИАС ИРЩШКХО ЬЙВОУОФДИФОЭЙС И ЙФШЩЭЭЙС Й ЬЩИШУВИЩОПИ, ХЬВЬЩЕШ, ФИКИПЬ, РОЮЩИПШВИД, ЮЬЙШЯОЭ ЧВИ ПШЙВО КЬ ВЬНЬ. ВОХ ПЕ АДИ ТОЙО КФО, И УО РОХОВШ ЮШЩШК УОПИ ЬВХЩЕДОЙС ЖНЩБПОЭ ПШЙВУЬЙВС, ШЯШ ЦЬДШШ ПЩОТУОЭ, ТШП ФЙШ, ТВЬ ПЕ ФИКШДИ КЬ ЙИЗ ЮЬЩ. ЧВЬ ЦЕД ЩЬК ЮДОВЬ, ЩОЙХИУЖФАШНЬЙЭ Ж ЮЬКУЬЫСЭ ЮЬТВИ УШЮЩИЙВЖЮУЬНЬ ЙХДЬУО И ЮЬЩЬЙАШНЬ ДШЙЬП ЬВ УИРЖ КЬ ЙОПЬНЬ ФШЩЗО. ЙХДЬУ ЦЕД ЖЙШЭУ НЩЬПОКУЕПИ ФОДЖУОПИ, ХЬВЬЩЕШ, ХОРОДЬЙС, ИШ ЮОКОДИ ФУИР Ф КЬДИУЖ ДИАС ЮЬВЬПЖ, ТВЬ КШЩШФСЭ ЮЩШНЩОЫКОДИ ИП ЮЖВС. НДЖЦЬХИШ ЩОЙЙШДИУЕ ЮШЩШЙШХОДИ ЮДОВЬ ФЬ ФЙШЗ УОЮЩОФДШУИЭЗ И ЮЩИКОФОДИ ЮШЛРОЫЖ ШЯШ ЦЬДСАЖБ КИХЬЙВС. ЮДЬЙХЬНЬЩСШ, ЮЬ ХЬВЬЩЬПЖ ПЕ ЮЬКУИПОДИЙС, ЙЮДЬАС ЮЬЩЬЙДЬ ШЫШФИХЬЛ. ФЙХЬЩШ ЙВОДЬ ЭЙУЬ, ТВЬ ЦШР ХЬЙЕ УОП ЙХФЬРС РОЩЬЙДИ УШ ЮЩЬЦЩОВСЙЭ. ЮЬ ЮЩИХОРЖ ДШНЩОУО БЮИВШЩ ЙВОД ФЕХОАИФОВС КДЭ УОЙ ВЩЬЮИУХЖ Х ВБДСЮОУЬФЬПЖ КШЩШФЖ УШЬЦЕХУЬФШУУЬЛ ФЕЙЬВЕ, ХЬВЬЩЬШ ЙВЬЭДЬ, ЬХЩЖЫШУУЬШ КШЙЭВХЬП КЖЦЬФ, И КОДШХЬ ЮЩШФЬЙЗЬКИДЬ И ЧВИ КЖЦЕ, И ФЬЬЦЯШ ФЙШ КШЩШФСЭ, ХОХИШ ПУШ ЮЩИЗЬКИДЬЙС ХЬНКО-ДИЦЬ ФИКШВС, ЩОЙХИКИЙВЬЛ ХЩЬУЬЛ, ФШДИТОФЬЛ ХЩОЙЬВЬЛ ДИЙВФЕ И ГОЩЙВФШУУЬЙВСБ ЬЦЯИЗ ЬТШЩВОУИЛ. ХЬНКО ПЕ ЮЩИАДИ УОХЬУШГ Х ГШДИ, ДШНЩОУ ЬЦШЩУЖДЙЭ Х БЮИВШЩЖ И ЙЮЩЬЙИД, ЙПЬЫШВ ДИ ЬУ ФРЬЦЩОВСЙЭ УО ЧВЬ КШЩШФЬ. ЙВОЩИХ ЦЕД ЙЮШЩФО ЬРОКОТШУ ФЬЮЩЬЙЬП И УИТШНЬ УШ ЬВФШВИД. ЮЬВЬП, ЮЬКЬЛКЭ Х ДШЙУЬПЖ НИНОУВЖ, ЬУ ЬЦЬАШД ЙВФЬД ХЩЖНЬП, ФУИПОВШДСУЬ ФНДЭКЕФОЭЙС. <...> ФЬ ФЩШПЭ ЧВЬНЬ КИОДЬНО БЮИВШЩ ЦЕД ЙХЩЕВ ДИЙВФЬЛ КШЩШФО. УЬ ЫЖХ, ХЬВЬЩЬНЬ ЬУ ЙЮЖЙВИД ФУИР, ФИКУШДЙЭ ВШЮШЩС УО ХЬУГШ АУЖЩХО. РОЗЬКЭЯШШ ЙЬДУГШ ШЯШ ЬЙФШЯОДЬ ФЬРФЕАШУУЬЙВС, НКШ ПЕ ЙВЬЭДИ, И Ф ЮЬЙДШКУИЗ ШНЬ ДЖТОЗ ЫЖХ ЙФШЩХУЖД, ХОХ ЮЬДИЩЬФОУУЕЛ РЬДЬВЬЛ АОЩИХ. ЬУ ЙФЬЦЬКУЬ ЙФИЙОД ПШЫКЖ ФШВФШЛ КШЩШФО, И ШЙДИ Ц БЮИВШЩ ЙШЛТОЙ ЬВЮЖЙВИД АУЖЩЬХ, ВЬВ ЖЮОД ЦЕ ЮЩЭПЬ Х УОАИП УЬНОП. ДШНЩОУ ЦЕЙВЩЬ ЙЗФОВИД ХЬЙЖ И ЩОЙТИЙВИД ЖТОЙВЬХ КИОПШВЩЬП Ф КШФЭВС - КФШУОКГОВС МЖВЬФ, ЮЬЙДШ ТШНЬ ЬУ ФШДШД БЮИВШЩЖ ЬВЮЖЙВИВС АУЖЩЬХ И ЙДШРОВС ЮЬЙХЬЩШШ ФУИР. РОЦИФ ХЬДЕАШХ ВЬТУЬ Ф ВЬП ПШЙВШ, ХЖКО ЖЮОД ЫЖХ, ПЬЛ КЩЖН ФЕВОЯИД ИР ХОЩПОУО РШПДШПШЩУЖБ ДШУВЖ. ЮЩИХЩШЮИФ ШШ РО ХЬУШГ Х ЙВФЬДЖ КШЩШФО, ХОХ ЩОР УОЮЩЬВИФ РОЦИВЬНЬ ХЬДЕАХО, ЬУ ЮЩЬВЭУЖД ШШ ЮЩЭПЬ, КЬ ХЬДЕАХО, ЮЬЙДШ ТШНЬ, ЮЩЬКЬДЫОЭ ЩОРПОВЕФОВС ДШУВЖ И ЬВЙВЖЮОЭ УОРОК, ЬВПШЩИД ШЯШ ЮЭВСКШЙЭВ МЖВЬФ. БЮИВШЩ Й ХЬЙЬЛ Ф ЩЖХОЗ АШД ЮШЩШК УИП, ЙЩШРОЭ ХЖЙВЕ ШЫШФИХИ. КЬЛКЭ КЬ УЖЫУЬНЬ ПШЙВО, ДШНЩОУ РОЦИД ШЯШ ЬКИУ ХЬДЕАШХ И, ЮЩИУИПОЭ ШНЬ РО ГШУВЩ, ЬТИЙВИД ХЩЖН КИОПШВЩЬП ЮЩИПШЩУЬ Ф ТШВЕЩШ МЖВО. ЮЬВЬП ЬУ КОД ЮЬ ДЬЮОВШ ПУШ И БЮИВШЩЖ, ЙОП ФРЭД ДЬЮОВЖ И ЮЩИХОРОД УОП ХЬЮОВС."
text = text.split("")

@tbl = {
  "А"=> "",
  "Б"=> "",
  "В"=> "т",
  "Г"=> "",
  "Д"=> "л",
  "Е"=> "",
  "Ж"=> "у",
  "З"=> "",
  "И"=> "и",
  "Й"=> "с",
  "К"=> "",
  "Л"=> "",
  "М"=> "",
  "Н"=> "",
  "О"=> "а",
  "П"=> "",
  "Р"=> "",
  "С"=> "ь",
  "Т"=> "",
  "У"=> "",
  "Ф"=> "",
  "Х"=> "",
  " "=> "",
  "Ч"=> "",
  "Ш"=> "",
  "Щ"=> "",
  "Ы"=> "",
  "Ь"=> "",
  "Э"=> "",
  "Ю"=> "п",
  "Я"=> "",
  " "=> " "
}

def solution(text)
  decipher_text = []
  text.each_with_index do |value|
    decipher_text << @tbl[value.to_s.downcase]
  end
  p decipher_text.join("")
end

solution(text)
